export default {
  name: 'BtnCalculator',
  props: {
    Rcolor: {
      type: String,
      default: 'warning'
    },
    Ecolor: {
      type: String,
      default: 'warning'
    }
  },
  data() {
    return {
      Value: '',
      Ans: '',
      showAlert: ''
    };
  },
  watch: {
    Value(newVal) {
      if (newVal === "+" || newVal === "-") {
        return this.showAlert = true;
      }
      return this.showAlert = false;
    }
  },
  methods: {
    reset: function () {
      this.Value = '';
      this.Ans = '';
    },
    Equals: function () {
      this.Ans = eval(this.Value);
    }
  }
};
