//functions to check if the value inp by user in string or any scpecial characters that're nor number

export function Value1(newVal) {
    //check if the inp is NaN
    if (isNaN(newVal)) {
        return this.V1Alert = true; // make the alert appear
    } else {
        return this.V1Alert = false; //make the alert disappear
    }
}

export function Value2(newVal) {
    if (isNaN(newVal)) {
        return this.V2Alert = true;
    } else {
        return this.V2Alert = false;
    }
}

export function Switch() {
    if (this.Switch) {
        this.MainID = 'MainID';
        this.ContainerID = 'ContainerID'
    }
    else {
        this.MainID = ''
        this.ContainerID = ''
    }
}
