export default {
  name: 'TextFiles',
  props: {
    textcolor: {
      type: String
    },
    textlabel: {
      type: String,
      default: 'Value'
    }
  },
  data() {
    return {
      rulesNumber: v => {
        if (!v.trim()) return true;
        if (!isNaN(parseFloat(v)) && v >= 0 && v <= 999) return true;
        return 'The Input must be in number !!!';
      }
    };
  }
};
