export default {
  props: {
    Rcolor: {
      type: String,
      required: true
    }
  },
  data() {
    return {
      Value1: '',
      Value2: '',
      showAlert: '',
      add: false,
      sub: false,
      multi: false,
      dev: false
    };
  },
  watch: {
    Value1: Value1,
    Value2: Value2
  },
  computed: {
    Ans() {
      //convert the inp value to number 
      let V1 = Number(this.Value1);
      let V2 = Number(this.Value2);

      //check operation condition
      if (this.add) {
        return V1 + V2;
      } else if (this.sub) {
        return V1 - V2;
      } else if (this.multi) {
        return V1 * V2;
      } else if (this.dev) {
        return V1 / V2;
      }
      return 0;
    }
  },
  methods: {
    reset: function () {
      this.Value1 = '';
      this.Value2 = '';
      this.add = this.sub = this.multi = this.dev = false;
      this.Ans = '';
    },
    ADD: function () {
      this.add = true;
      this.sub = this.multi = this.dev = false;
    },
    SUB: function () {
      this.sub = true;
      this.add = this.multi = this.dev = false;
    },
    MULTI: function () {
      this.multi = true;
      this.sub = this.add = this.dev = false;
    },
    DEV: function () {
      this.dev = true;
      this.sub = this.multi = this.add = false;
    }

